use strict;my $batch = 5;my $iteration = 202;my $indexFileNumber = 1010;while(1){	#Process $batch documents	for(my $index=0; $index<$batch; $index++)	{		my $file = "../Crawler/texts/".$indexFileNumber.".txt";		print "\n\nUsing file $indexFileNumber.txt\n\n";		$indexFileNumber++;		#PRE PROCESSOR - It sentences the text		system("perl preProcessor.pl $iteration $file");		#SEGMENTER		system("perl segmenter.pl $iteration");		#RELATION IDENTIFIER		system("perl controllerModel.pl $iteration");			#Handling files to historic		#system('cp "'.$file.'" "usedTexts/"');	}		#INCREMENTER - increments the model with examples whose probability are higher than a threshold	system("perl incrementModel.pl $iteration");				#MONITOR - analyses the evaluation curve and decides what to do i) continue ii) stop or iii) modify forget and learn	system("perl analyseLearning.pl $iteration");					$iteration++;	#disk optimization			if (!(-e "classifications.zip"))	{		system("zip -r classifications.zip classifications");		system("rm classifications/*");	}		opendir (DIR, "classifications") or die "Error: can't open directory classifications $!\n";	my @listFiles = grep {(!(/^\./))} readdir (DIR);	closedir (DIR);	foreach(@listFiles)	{		system("zip -u classifications.zip classifications/$_");		unlink("classifications/$_");	}}